package javatopics;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a =170;
		int b=166;
		boolean x = true;
		boolean y = false;
		
		//Arithmetic operators
		
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);
		
		//Relationl opertrs >> Always return true / false 
		
		System.out.println(a<b);  
		System.out.println(a>b);
		System.out.println(a<=b);
		System.out.println(a>=b);
		System.out.println(a!=b);
		System.out.println(a==b); 
		
		// Logical operators  >> Operands are always boolean >> Always return true / false 
		
		System.out.println(x && y);  
		System.out.println(x || y);  
		System.out.println(!x);  
		
		//Assignment operators 
		
		// = += -= *+ /= 
		
		// increamnet and decreament oprts  a++ // a--
		
	
	}

}
